import React, { useEffect } from "react";
import { useAppDispatch, useAppSelector } from "../redux/hook";
import { fetchCity } from "../redux/SliceApi";
import { useParams } from "react-router-dom";
import Style from "./serachfilter.module.scss";

const SerachFilterData = () => {
  const { userData } = useParams();
  const dispatch = useAppDispatch();
  const data = useAppSelector((state) => state.detail);
  useEffect(() => {
    dispatch(fetchCity());
  }, [dispatch]);
  const filterData =
    data.cityData && data.cityData.filter((item) => item.city === userData);
  console.log(filterData, "hello");
  return (
    <div style={{ marginTop: "200px" }}>
      {filterData &&
        filterData.map((item) => {
          return (
            <div className={Style.content}>
              <h2>
                {item.name}
                <span>{item.city}</span>
              </h2>
              <div className={Style.image}>
                {item.image &&
                  item.image.map((item) => {
                    return (
                      <span>
                        <img src={item} alt="" />
                      </span>
                    );
                  })}
              </div>
              <div className={Style.paragraph}>
                <p>{item.detail}</p>
              </div>
            </div>
          );
        })}
    </div>
  );
};

export default SerachFilterData;
