import React, { useEffect } from "react";
import { useAppDispatch, useAppSelector } from "../redux/hook";
import { fetchAbout } from "../redux/SliceApi";
import Style from "./about.module.scss";
import OwlCarousel from "react-owl-carousel";
import "owl.carousel/dist/assets/owl.carousel.css";
import "owl.carousel/dist/assets/owl.theme.default.css";

const About = () => {
  const dispatch = useAppDispatch();
  const data = useAppSelector((state) => state.detail);
  useEffect(() => {
    dispatch(fetchAbout());
  }, [dispatch]);

  const filterData =
    data.aboutData && data.aboutData.filter((item) => item.url === userAbout);
  console.log(filterData, "hello");

  return (
    <div className={Style.about}>
      {data.aboutData &&
        data.aboutData.map((item) => {
          return (
            <div className={Style.data}>
              <span>
                <img src={item.img} alt="about" />
                <div className={Style.heading}>
                  <h2>{item.title}</h2>
                </div>
              </span>
              <div className={Style.content}>
                <div className={Style.image}>
                  <OwlCarousel
                    items={1}
                    loop
                    autoplay
                    autoplayTimeout={3000}
                    dots={false}
                  >
                    {item.image &&
                      item.image.map((item) => (
                        <span>
                          <img src={item} alt="house" />
                        </span>
                      ))}
                  </OwlCarousel>
                </div>

                <div className={Style.text}>
                  <h2>{item.heading}</h2>
                  {item.paragraph &&
                    item.paragraph.map((item) => <p>{item.content}</p>)}
                </div>
              </div>
              <div className={Style.mainbox}>
                <div className={Style.project}>
                  <div className={Style.detail}>
                    <h2>{item.project}</h2>
                    <ul>
                      {item.data &&
                        item.data.map((item) => {
                          return (
                            <li>
                              <a href={item.url}>{item.title}</a>
                            </li>
                          );
                        })}
                    </ul>
                  </div>
                  <div className={Style.collection}>
                    {item.detail &&
                      item.detail.map((item) => {
                        return (
                          <strong>
                            <img src={item.img} alt="project" />
                            <h2>{item.title}</h2>
                            <p>{item.paragraph}</p>
                          </strong>
                        );
                      })}
                  </div>
                </div>
              </div>
            </div>
          );
        })}
    </div>
  );
};

export default About;
