import React, { useEffect } from "react";
import { useAppDispatch, useAppSelector } from "../redux/hook";
import { fetchAbout } from "../redux/SliceApi";
import { useLocation } from "react-router-dom";

const About = () => {
  const location = useLocation();
  const dispatch = useAppDispatch();
  const data = useAppSelector((state) => state.detail);

  useEffect(() => {
    dispatch(fetchAbout());
  }, [dispatch]);

  const queryParams = new URLSearchParams(location.search);
  const id: any = queryParams.get("id");

  const filterData = data.aboutData
    ? data.aboutData.filter((item) => item.id === parseInt(id, 10))
    : [];

  return (
    <div>
      {filterData.length > 0 ? (
        <>
          <h1>{filterData[0].title}</h1>
          {filterData[0].paragraph.map((para, index) => (
            <p key={index}>{para.content}</p>
          ))}
          {/* Add more content rendering based on your data structure */}
        </>
      ) : (
        <p>No data found</p>
      )}
    </div>
  );
};

export default About;
