import React, { useEffect } from "react";
import { useAppDispatch, useAppSelector } from "../redux/hook";
import { fetchAbout } from "../redux/SliceApi";
import { useParams } from "react-router-dom";

const About = () => {
  const { userAbout } = useParams<{ userAbout: string }>();
  const dispatch = useAppDispatch();
  const data = useAppSelector((state) => state.detail);

  useEffect(() => {
    dispatch(fetchAbout());
  }, [dispatch]);

  // Filter the data based on the userAbout parameter
  const filterData = data.aboutData
    ? data.aboutData.find((item) => item.url === `/about-us/${userAbout}`)
    : null;

  if (!filterData) {
    return <div>Loading...</div>; // Handle loading state appropriately
  }

  return (
    <div>
      <h1>{filterData.title}</h1>
      {filterData.paragraph.map((para) => (
        <p key={para.id}>{para.content}</p>
      ))}
    </div>
  );
};

export default About;
