import React, { useEffect, useState } from "react";
import { useAppDispatch, useAppSelector } from "../redux/hook";
import { fetchFooter } from "../redux/SliceApi";
import Style from "./footer.module.scss";

interface IType {
  email: string;
}

const Footer = () => {
  const [detail, setDetail] = useState<IType>({
    email: "",
  });

  const handleData = (e: any) => {
    const newData = { ...detail, [e.target.id]: e.target.value };
    setDetail(newData);
  };
  const mydata = (e: any) => {
    e.preventDefault();

    const object: RequestInit = {
      method: "POST",
      body: JSON.stringify({
        email: detail.email,
      }),
    };
    fetch("http://localhost:5000/post", object).then((res) => res.json());
  };

  const dispatch = useAppDispatch();
  const data = useAppSelector((state) => state.detail);
  useEffect(() => {
    dispatch(fetchFooter());
  }, [dispatch]);
  return (
    <div className={Style.footer}>
      <div className={Style.footerTop}>
        {data.footerData &&
          data.footerData.map((item) => {
            return (
              <div className={Style.foot}>
                <h2>
                  {item.heading}
                  <i></i>
                </h2>

                <ul>
                  {item.data &&
                    item.data.map((item) => {
                      return (
                        <li>
                          <a href="#/">{item.address}</a>
                        </li>
                      );
                    })}
                </ul>
                {Number(item.id) === 3 && (
                  <div className={Style.inputBox}>
                    <form onSubmit={mydata}>
                      <input
                        type="text"
                        placeholder="Your Email (required)"
                        id="email"
                        value={detail.email}
                        onChange={handleData}
                      />
                      <br />
                      <button>SignUp</button>
                    </form>
                  </div>
                )}
              </div>
            );
          })}
      </div>
    </div>
  );
};

export default Footer;
